        background: linear-gradient(135deg, rgba(0, 217, 255, 0.1), rgba(168, 85, 247, 0.1));
        border-radius: var(--radius-md);
        display: flex;
        align-items: center;
        justify-content: center;
    }

    .card-header-icon svg {
        width: 20px;
        height: 20px;
        stroke: var(--accent-cyan);
    }

    .card-title {
        font-size: var(--text-lg);
        font-weight: var(--font-bold);
        color: #ffffff;
    }

    .card-body {
        padding: 24px;
    }

    /* Info Rows */
    .info-row {
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: 14px 0;
        border-bottom: 1px solid rgba(255, 255, 255, 0.06);
    }

    .info-row:last-child {
        border-bottom: none;
        padding-bottom: 0;
    }

    .info-row:first-child {
        padding-top: 0;
    }

    .info-label {
        color: rgba(255, 255, 255, 0.7);
        font-size: var(--text-sm);
        font-weight: var(--font-medium);
    }

    .info-value {
        color: #ffffff;
        font-size: var(--text-sm);
        font-weight: var(--font-semibold);
        text-align: right;
    }

    /* Feature List */
    .feature-list {
        display: flex;
        flex-direction: column;
        gap: 12px;
    }

    .feature-item {
        display: flex;
        align-items: center;
        justify-content: space-between;
        padding: 12px 16px;
        background: rgba(255, 255, 255, 0.03);
        border-radius: var(--radius-md);
        transition: all var(--transition-fast);
    }

    .feature-item:hover {
        background: rgba(255, 255, 255, 0.06);
    }

    .feature-name {
        color: #ffffff;
        font-size: var(--text-sm);
    }

    .feature-status {
        display: flex;
        align-items: center;
        gap: 6px;
        font-size: var(--text-sm);
        font-weight: var(--font-medium);
    }

    .feature-status.enabled {
        color: var(--success);
    }

    .feature-status.disabled {
        color: var(--text-tertiary);
    }

    .feature-status svg {
        width: 16px;
        height: 16px;
    }

    /* Status Badge */
    .status-badge {
        display: inline-flex;
        align-items: center;
        gap: 6px;
        padding: 4px 10px;
        border-radius: var(--radius-full);
        font-size: var(--text-xs);
        font-weight: var(--font-semibold);
    }

    .status-badge.active {
        background: rgba(34, 197, 94, 0.1);
        color: var(--success);
        border: 1px solid rgba(34, 197, 94, 0.2);
    }

    .status-badge.inactive {
        background: rgba(239, 68, 68, 0.1);
        color: var(--danger);
        border: 1px solid rgba(239, 68, 68, 0.2);
    }

    .status-dot {
        width: 6px;
        height: 6px;
        border-radius: 50%;
        background: currentColor;
    }

    /* Form Styles */
    .form-group {
        margin-bottom: 20px;
    }

    .form-label {
        display: block;
        margin-bottom: 8px;
        color: #ffffff;
        font-size: var(--text-sm);
        font-weight: var(--font-medium);
    }

    .form-input {
        width: 100%;
        padding: 12px 16px;
        background: rgba(0, 0, 0, 0.3);
        border: 1px solid var(--border-subtle);
        border-radius: var(--radius-md);
        color: var(--text-primary);
        font-family: inherit;
        font-size: var(--text-sm);
        transition: all var(--transition-fast);
    }

    .form-input:focus {
        outline: none;
        border-color: var(--accent-cyan);
        box-shadow: 0 0 0 3px rgba(0, 217, 255, 0.1);
    }

    .form-input::placeholder {
        color: var(--text-tertiary);
    }

    .btn-update {
        width: 100%;
        padding: 14px 24px;
        background: linear-gradient(135deg, var(--accent-cyan), var(--accent-purple));
        border: none;
        border-radius: var(--radius-md);
        color: #000;
        font-size: var(--text-sm);
        font-weight: var(--font-bold);
        cursor: pointer;
        transition: all var(--transition-fast);
    }

    .btn-update:hover {
        transform: translateY(-2px);
        box-shadow: 0 8px 24px rgba(0, 217, 255, 0.3);
    }

    /* Responsive */
    @media (max-width: 1024px) {
        .account-grid {
            grid-template-columns: 1fr;
        }
    }

    @media (max-width: 768px) {
        .profile-header {
            flex-direction: column;
            text-align: center;
            padding: 32px 24px;
        }

        .profile-meta {
            justify-content: center;
        }

        .admin-link {
            margin: 16px auto 0;
        }
    }
</style>
{% endblock %}

{% block content %}
<!-- Profile Header -->
<div class="profile-header">
    <div class="profile-avatar"><span>{{ user.username[0]|upper }}</span></div>
    <div class="profile-info">
        <h1>{{ user.username }}</h1>
        <div class="profile-email">{{ user.email }}</div>
        <div class="profile-meta">
            <span class="profile-badge {{ user.subscription_tier }}">{{ user.subscription_tier|title }}</span>
            <span class="member-since">Member since {{ user.created_at.strftime('%B %Y') if user.created_at else 'N/A' }}</span>
        </div>
    </div>
    {% if user.subscription_tier == 'developer' %}
    <a href="/auth/admin/dashboard" class="admin-link">
        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
            <rect x="3" y="11" width="18" height="11" rx="2" ry="2"/>
            <path d="M7 11V7a5 5 0 0 1 10 0v4"/>
        </svg>
        Admin Dashboard
    </a>
    {% endif %}
</div>

<div class="account-grid">
    <!-- Subscription Card -->
    <div class="account-card">
        <div class="card-header">
            <div class="card-header-icon">
                <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                    <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"/>
                    <circle cx="12" cy="7" r="4"/>
                </svg>
            </div>
            <h2 class="card-title">Subscription</h2>
        </div>
        <div class="card-body">
            <div class="info-row">
                <span class="info-label">Current Plan</span>
                <span class="info-value">
                    <span class="profile-badge {{ user.subscription_tier }}">{{ user.subscription_tier|title }}</span>
                </span>
            </div>
            <div class="info-row">
                <span class="info-label">Status</span>
                <span class="info-value">
                    <span class="status-badge {{ user.subscription_status }}">
                        <span class="status-dot"></span>
                        {{ user.subscription_status|title }}
                    </span>
                </span>
            </div>
            {% if user.subscription_tier == 'free' %}
            <div class="info-row">
                <span class="info-label">Daily Signals</span>
                <span class="info-value">3 signals</span>
            </div>
            {% elif user.subscription_tier == 'pro' %}
            <div class="info-row">
                <span class="info-label">Daily Signals</span>
                <span class="info-value">Unlimited</span>
            </div>
            <div class="info-row">
                <span class="info-label">Price</span>
                <span class="info-value">$19.99/month</span>
            </div>
            {% elif user.subscription_tier == 'premium' %}
            <div class="info-row">
                <span class="info-label">Daily Signals</span>
                <span class="info-value">Unlimited + API</span>
            </div>
            <div class="info-row">
                <span class="info-label">Price</span>
                <span class="info-value">$49.99/month</span>
            </div>
            {% elif user.subscription_tier == 'developer' %}
            <div class="info-row">
                <span class="info-label">Access Level</span>
                <span class="info-value">Full Access</span>
            </div>
            <div class="info-row">
                <span class="info-label">Price</span>
                <span class="info-value">Complimentary</span>
            </div>
            {% endif %}
        </div>
    </div>

    <!-- Features Card -->
    <div class="account-card">
        <div class="card-header">
            <div class="card-header-icon">
                <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                    <path d="M13 3L4 14h7l-2 7 9-11h-7l2-7z"/>
                </svg>
            </div>
            <h2 class="card-title">Your Features</h2>
        </div>
        <div class="card-body">
            <div class="feature-list">
                <div class="feature-item">
                    <span class="feature-name">Daily Signals</span>
                    <span class="feature-status enabled">
                        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                            <path d="M20 6L9 17l-5-5"/>
                        </svg>
                        {{ 'Unlimited' if user.is_pro() else '3/day' }}
                    </span>
                </div>
                <div class="feature-item">
                    <span class="feature-name">30-Day History</span>
                    <span class="feature-status {{ 'enabled' if user.is_pro() else 'disabled' }}">
                        {% if user.is_pro() %}
                        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                            <path d="M20 6L9 17l-5-5"/>
                        </svg>
                        Enabled
                        {% else %}
                        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                            <path d="M18 6L6 18M6 6l12 12"/>
                        </svg>
                        Pro Only
                        {% endif %}
                    </span>
                </div>
                <div class="feature-item">
                    <span class="feature-name">Email Alerts</span>
                    <span class="feature-status {{ 'enabled' if user.is_pro() else 'disabled' }}">
                        {% if user.is_pro() %}
                        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                            <path d="M20 6L9 17l-5-5"/>
                        </svg>
                        Enabled
                        {% else %}
                        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                            <path d="M18 6L6 18M6 6l12 12"/>
                        </svg>
                        Pro Only
                        {% endif %}
                    </span>
                </div>
                <div class="feature-item">
                    <span class="feature-name">CSV Export</span>
                    <span class="feature-status {{ 'enabled' if user.is_pro() else 'disabled' }}">
                        {% if user.is_pro() %}
                        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                            <path d="M20 6L9 17l-5-5"/>
                        </svg>
                        Enabled
                        {% else %}
                        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                            <path d="M18 6L6 18M6 6l12 12"/>
                        </svg>
                        Pro Only
                        {% endif %}
                    </span>
                </div>
                <div class="feature-item">
                    <span class="feature-name">API Access</span>
                    <span class="feature-status {{ 'enabled' if user.subscription_tier in ['premium', 'developer'] else 'disabled' }}">
                        {% if user.subscription_tier in ['premium', 'developer'] %}
                        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                            <path d="M20 6L9 17l-5-5"/>
                        </svg>
                        Enabled
                        {% else %}
                        <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                            <path d="M18 6L6 18M6 6l12 12"/>
                        </svg>
                        Premium Only
                        {% endif %}
                    </span>
                </div>
            </div>
        </div>
    </div>

    <!-- Account Details Card -->
    <div class="account-card">
        <div class="card-header">
            <div class="card-header-icon">
                <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                    <circle cx="12" cy="12" r="10"/>
                    <path d="M12 16v-4M12 8h.01"/>
                </svg>
            </div>
            <h2 class="card-title">Account Details</h2>
        </div>
        <div class="card-body">
            <div class="info-row">
                <span class="info-label">Email</span>
                <span class="info-value" style="font-family: 'JetBrains Mono', monospace; font-size: var(--text-xs);">{{ user.email }}</span>
            </div>
            <div class="info-row">
                <span class="info-label">Username</span>
                <span class="info-value">{{ user.username }}</span>
            </div>
            <div class="info-row">
                <span class="info-label">Member Since</span>
                <span class="info-value">{{ user.created_at.strftime('%B %d, %Y') if user.created_at else 'N/A' }}</span>
            </div>
            <div class="info-row">
                <span class="info-label">Account Type</span>
                <span class="info-value">{{ 'Password' if user.password_hash else 'OAuth' }}</span>
            </div>
        </div>
    </div>

    <!-- Password Change Card -->
    {% if user.password_hash %}
    <div class="account-card">
        <div class="card-header">
            <div class="card-header-icon">
                <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                    <rect x="3" y="11" width="18" height="11" rx="2" ry="2"/>
                    <path d="M7 11V7a5 5 0 0 1 10 0v4"/>
                </svg>
            </div>
            <h2 class="card-title">Change Password</h2>
        </div>
        <div class="card-body">
            <form method="POST" action="/auth/change-password">
        {{ csrf_token() }}
                <div class="form-group">
                    <label class="form-label">Current Password</label>
                    <input type="password" name="current_password" required class="form-input" placeholder="Enter current password">
                </div>
                <div class="form-group">
                    <label class="form-label">New Password</label>
                    <input type="password" name="new_password" required minlength="6" class="form-input" placeholder="Enter new password">
                </div>
                <div class="form-group">
                    <label class="form-label">Confirm New Password</label>
                    <input type="password" name="confirm_password" required minlength="6" class="form-input" placeholder="Confirm new password">
                </div>
                <button type="submit" class="btn-update">Update Password</button>
            </form>
        </div>
    </div>
    {% endif %}
</div>
{% endblock %}

{% block extra_scripts %}
<script src="{{ url_for('static', filename='toast.js') }}"></script>
<script src="{{ url_for('static', filename='ui-enhancements.js') }}"></script>
{% endblock %}
